from manim import *

class AnimatingExample(Scene):
    def construct(self):
        square = Square()

        # the square scales and rotates
        self.play(
            square.animate.scale(2).rotate(PI/2)
        )

        # this does the same thing, explicitly
        self.play(
            Animation(square, final_mobject=square.copy().scale(2).rotate(PI/2))
        )
